// src/components/MessageComponent.js

import React, { useEffect, useState } from 'react';
import apiService from '../services/apiService'; // Stelle sicher, dass dieser Pfad korrekt ist

const MessageComponent = () => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchedMessages = apiService.getMessages();
    setMessages(fetchedMessages);
  }, []);

  const handleSaveMessage = (message) => {
    apiService.saveMessage(message);
    setMessages([...messages, message]);
  };

  return (
    <div>
      <h1>Nachrichten</h1>
      <ul>
        {messages.map((msg, index) => (
          <li key={index}>{msg}</li>
        ))}
      </ul>
      <button onClick={() => handleSaveMessage("Neue Nachricht")}>Nachricht speichern</button>
    </div>
  );
};

export default MessageComponent;
